          highlights: [
            {
              score: 1.0330637693405151,
              path: 'description',
              texts: [
                { value: 'Apples come in several ', type: 'text' },
                { value: 'varieties', type: 'hit' },
                {
                  value: ', including Fuji, Granny Smith, and Honeycrisp. ',
