           "mustNot": [
           {
           "equals": {
           "path": "_id",
            "value": ObjectId ("573a1394f29313caabcde9ef")
          }
         }]
       }
        }
        },
       { $limit: 10 },
                {
       $project: {
           "title": 1,
           "genres": 1,
           "_id": 1
