     {
       "$search": {
         "range": {
           "path": "title",
           "gt": "city",
           "lt": "country"
         }
       }
     },
     {
       "$limit": 5
     },
     {
       "$project": {
         "_id": 0,
         "title": 1
       }
     }
   ])

