      db.movies.aggregate([
        {
          "$search": {
            "range": {
              "path": "released",
              "gt": ISODate("2015-01-01T00:00:00.000Z"),
              "lt": ISODate("2015-12-31T00:00:00.000Z")
            },
            "sort": {
              "_id": -1
            }
          }
        },
        {
          "$limit": 5
        },
        {
          "$project": {
            "_id": 1,
            "title": 1,
            "released": 1,
            "score": { "$meta": "searchScore" }
