     db.inventory.find({ "product_attributes" : { "price" : 29.99 } } )
     db.inventory.find({ "product_attributes.tags" : [ "waterproof", "fireproof" ] } )

     db.inventory.aggregate([{ 
       $match : { "product_attributes" : { "price" : 29.99 } }
