      "description" : "Single LINE description.",
      "returnObject" : [ { "match" : "LINE", "idx" : 7, "captures" : [ ] } ]
   }
   {
      "_id" : 2,
      "description" : "First lines\nsecond line",
      "returnObject" : [ { "match" : "line", "idx" : 6, "captures" : [ ] }, { "match" : "line", "idx" : 19, "captures" : [ ] } ]
