      "_id" : 4,
      "description" : "Multiple\nline descriptions",
      "returnObject" : [ { "match" : "line", "idx" : 9, "captures" : [ ] } ]
   }
   {
      "_id" : 5,
      "description" : "anchors, links and hyperlinks",
      "returnObject" : [ ]
   }
   { "_id" : 6, "description" : "mtier work vocation", "returnObject" : [ ] }


